-- 导入静态数据到数据库

-- 1. 导入标签
ON CONFLICT (name) DO NOTHING;

-- 2. 导入文章（读书感悟）
ON CONFLICT (id) DO NOTHING;

-- 3. 创建文章-标签关联
SELECT 1, id FROM tags WHERE name IN ('岁月静好', '生活感悟', '内心平静');
SELECT 2, id FROM tags WHERE name IN ('小王子', '人生感悟', '爱与责任');
SELECT 3, id FROM tags WHERE name IN ('文字', '文学', '情感表达');
SELECT 4, id FROM tags WHERE name IN ('深夜读书', '阅读体验', '内心宁静');
SELECT 5, id FROM tags WHERE name IN ('文学医学', '跨界思考', '同理心');
SELECT 6, id FROM tags WHERE name IN ('散文', '真实', '生活感悟');
SELECT 7, id FROM tags WHERE name IN ('小说', '人性', '文学思考');
SELECT 8, id FROM tags WHERE name IN ('读书', '成长', '改变');

-- 4. 导入书籍推荐
ON CONFLICT (id) DO NOTHING;

