INSERT INTO novels (id, novel_title, synopsis, chapter_count, last_update, status, cover_image, is_published) VALUES
(1, '春日暖阳', '一个关于青春与梦想的故事。在山西中医药大学的校园里，几个即将毕业的学生在春日里寻找着各自的人生方向。主人公林小暖是一个温柔善良的针灸推拿专业学生，她用自己的方式温暖着身边的每一个人。', 5, '2024-03-20', '连载中', '/imgs/literature_atmosphere_2.jpg', true),
(2, '药香里的诗', '一个融合中医与文学的故事。主人公苏诗雨是一个热爱文学的医学生，在学习针灸推拿的过程中，她发现中医的穴位名称都充满了诗意，于是开始用诗歌记录自己的学医感悟。', 4, '2024-03-18', '连载中', '/imgs/literature_atmosphere_7.jpg', true),
(3, '时光咖啡馆', '一个关于都市情感的故事。在太原的一个小咖啡馆里，不同背景的人们相遇，分享着各自的故事。主人公陈时光是一个文学爱好者，在咖啡馆里遇到了各种有趣的人，也找到了自己的人生方向。', 3, '2024-03-15', '连载中', '/imgs/literature_atmosphere_3.jpg', true),
(4, '远方来信', '一个书信体小说。主人公林小暖收到了一封来自远方的信，信中诉说着一个关于梦想与坚持的故事。通过这些信件，我们看到了一个年轻人对生活的热爱和对梦想的执着。', 4, '2024-03-12', '连载中', '/imgs/literature_atmosphere_7.jpg', true)
