INSERT INTO chapters (novel_id, chapter_number, chapter_title, content, publish_date, word_count, excerpt, is_published) VALUES
(1, 1, '初春的相遇', E'三月的太原，春寒料峭。山西中医药大学的校园里，梧桐树刚刚抽出嫩芽。林小暖背着书包，走在通往图书馆的小径上，心情却如这春日般温暖...\n\n（章节正文内容待补充...）\n\n这是一个温暖的故事，讲述了青春与梦想，友情与成长。每一个字都饱含着真挚的情感，希望能够温暖每一位读者的心。', '2024-03-01', 2800, '三月的太原，春寒料峭。山西中医药大学的校园里，梧桐树刚刚抽出嫩芽。林小暖背着书包，走在通往图书馆的小径上，心情却如这春日般温暖...', true),
(1, 2, '图书馆的偶遇', E'图书馆里总是很安静，只有偶尔翻书的声音。林小暖习惯性地走向她常坐的位置，却发现那里已经坐着一个人...\n\n（章节正文内容待补充...）\n\n这是一个温暖的故事，讲述了青春与梦想，友情与成长。每一个字都饱含着真挚的情感，希望能够温暖每一位读者的心。', '2024-03-05', 3200, '图书馆里总是很安静，只有偶尔翻书的声音。林小暖习惯性地走向她常坐的位置，却发现那里已经坐着一个人...', true),
(1, 3, '深夜的台灯下', E'宿舍里，室友们都已入睡，只有林小暖还在台灯下复习着明天的考试内容。窗外春风徐来，桃花飘香...\n\n（章节正文内容待补充...）\n\n这是一个温暖的故事，讲述了青春与梦想，友情与成长。每一个字都饱含着真挚的情感，希望能够温暖每一位读者的心。', '2024-03-10', 2900, '宿舍里，室友们都已入睡，只有林小暖还在台灯下复习着明天的考试内容。窗外春风徐来，桃花飘香...', true),
(1, 4, '春游的约定', E'春天来了，校园里的花开得正艳。朋友们提议一起去春游，林小暖欣然同意。她知道，这将是一个美好的春日...\n\n（章节正文内容待补充...）\n\n这是一个温暖的故事，讲述了青春与梦想，友情与成长。每一个字都饱含着真挚的情感，希望能够温暖每一位读者的心。', '2024-03-15', 3100, '春天来了，校园里的花开得正艳。朋友们提议一起去春游，林小暖欣然同意。她知道，这将是一个美好的春日...', true),
(1, 5, '毕业前的思考', E'随着毕业的临近，林小暖开始思考自己的未来。是留在太原，还是回到家乡？是在医院工作，还是自己开诊所？\n\n（章节正文内容待补充...）\n\n这是一个温暖的故事，讲述了青春与梦想，友情与成长。每一个字都饱含着真挚的情感，希望能够温暖每一位读者的心。', '2024-03-20', 3400, '随着毕业的临近，林小暖开始思考自己的未来。是留在太原，还是回到家乡？是在医院工作，还是自己开诊所？', true);
INSERT INTO chapters (novel_id, chapter_number, chapter_title, content, publish_date, word_count, excerpt, is_published) VALUES
(2, 1, '百会穴的诗', E'百会穴，位于头顶正中，如诗如画的名字让苏诗雨想起了李白的那句"举头望明月，低头思故乡"...\n\n（章节正文内容待补充...）\n\n这是一个温暖的故事，讲述了青春与梦想，友情与成长。每一个字都饱含着真挚的情感，希望能够温暖每一位读者的心。', '2024-03-01', 2500, '百会穴，位于头顶正中，如诗如画的名字让苏诗雨想起了李白的那句"举头望明月，低头思故乡"...', true),
(2, 2, '太冲穴的温柔', E'太冲穴，在足背上，如它的名字一样，给人一种冲和的感觉。苏诗雨想，这或许就是中医所说的"冲气以为和"...\n\n（章节正文内容待补充...）\n\n这是一个温暖的故事，讲述了青春与梦想，友情与成长。每一个字都饱含着真挚的情感，希望能够温暖每一位读者的心。', '2024-03-08', 2700, '太冲穴，在足背上，如它的名字一样，给人一种冲和的感觉。苏诗雨想，这或许就是中医所说的"冲气以为和"...', true),
(2, 3, '足三里的智慧', E'足三里，一个耳熟能详的穴位。老师说它是"长寿穴"，苏诗雨想，这名字本身就充满了对生命的敬畏...\n\n（章节正文内容待补充...）\n\n这是一个温暖的故事，讲述了青春与梦想，友情与成长。每一个字都饱含着真挚的情感，希望能够温暖每一位读者的心。', '2024-03-13', 2900, '足三里，一个耳熟能详的穴位。老师说它是"长寿穴"，苏诗雨想，这名字本身就充满了对生命的敬畏...', true),
(2, 4, '神门穴的宁静', E'神门穴，在腕横纹上，如它的名字一样，给人一种宁静的感觉。苏诗雨想，这或许就是古人所说的"心静如水"...\n\n（章节正文内容待补充...）\n\n这是一个温暖的故事，讲述了青春与梦想，友情与成长。每一个字都饱含着真挚的情感，希望能够温暖每一位读者的心。', '2024-03-18', 2600, '神门穴，在腕横纹上，如它的名字一样，给人一种宁静的感觉。苏诗雨想，这或许就是古人所说的"心静如水"...', true);
INSERT INTO chapters (novel_id, chapter_number, chapter_title, content, publish_date, word_count, excerpt, is_published) VALUES
(3, 1, '咖啡馆的午后', E'三月的午后，阳光透过咖啡馆的玻璃窗洒在木质桌椅上。陈时光点了一杯拿铁，静静地坐在角落里...\n\n（章节正文内容待补充...）\n\n这是一个温暖的故事，讲述了青春与梦想，友情与成长。每一个字都饱含着真挚的情感，希望能够温暖每一位读者的心。', '2024-03-01', 3000, '三月的午后，阳光透过咖啡馆的玻璃窗洒在木质桌椅上。陈时光点了一杯拿铁，静静地坐在角落里...', true),
(3, 2, '深夜的对话', E'深夜的咖啡馆里，客人已经很少了。陈时光还在那里写着什么，突然听到一个温和的声音...\n\n（章节正文内容待补充...）\n\n这是一个温暖的故事，讲述了青春与梦想，友情与成长。每一个字都饱含着真挚的情感，希望能够温暖每一位读者的心。', '2024-03-08', 3200, '深夜的咖啡馆里，客人已经很少了。陈时光还在那里写着什么，突然听到一个温和的声音...', true),
(3, 3, '春日的约定', E'春天来了，咖啡馆里开始有了新的客人。陈时光发现，生活就像这咖啡一样，苦中带甜...\n\n（章节正文内容待补充...）\n\n这是一个温暖的故事，讲述了青春与梦想，友情与成长。每一个字都饱含着真挚的情感，希望能够温暖每一位读者的心。', '2024-03-15', 3100, '春天来了，咖啡馆里开始有了新的客人。陈时光发现，生活就像这咖啡一样，苦中带甜...', true);
INSERT INTO chapters (novel_id, chapter_number, chapter_title, content, publish_date, word_count, excerpt, is_published) VALUES
(4, 1, '第一封信', E'亲爱的小暖：\n\n你好！我是你从未见过的朋友，但我从你的文字中感受到了你的温暖...\n\n（章节正文内容待补充...）\n\n这是一个温暖的故事，讲述了青春与梦想，友情与成长。每一个字都饱含着真挚的情感，希望能够温暖每一位读者的心。', '2024-03-01', 1800, '亲爱的小暖：\n\n你好！我是你从未见过的朋友，但我从你的文字中感受到了你的温暖...', true),
(4, 2, '关于梦想的信', E'小暖：\n\n你说你在学医的路上有时会迷茫，我想告诉你，迷茫是成长的必经之路...\n\n（章节正文内容待补充...）\n\n这是一个温暖的故事，讲述了青春与梦想，友情与成长。每一个字都饱含着真挚的情感，希望能够温暖每一位读者的心。', '2024-03-05', 2000, '小暖：\n\n你说你在学医的路上有时会迷茫，我想告诉你，迷茫是成长的必经之路...', true),
(4, 3, '春天的信', E'亲爱的朋友：\n\n春天来了，我想和你分享一些关于春天的故事...\n\n（章节正文内容待补充...）\n\n这是一个温暖的故事，讲述了青春与梦想，友情与成长。每一个字都饱含着真挚的情感，希望能够温暖每一位读者的心。', '2024-03-09', 1900, '亲爱的朋友：\n\n春天来了，我想和你分享一些关于春天的故事...', true),
(4, 4, '毕业的信', E'小暖：\n\n听说你要毕业了，我想告诉你，无论你选择什么样的道路，都要相信自己的选择...\n\n（章节正文内容待补充...）\n\n这是一个温暖的故事，讲述了青春与梦想，友情与成长。每一个字都饱含着真挚的情感，希望能够温暖每一位读者的心。', '2024-03-12', 2100, '小暖：\n\n听说你要毕业了，我想告诉你，无论你选择什么样的道路，都要相信自己的选择...', true);
